const Corona=()=>{
    const[Country,setCountry]=useState();
    const[TotalConfirmed,setConfirmed]=useState();
    const[TotalRecovered,setRecovered]=useState();
    const[TotalDeaths,setDeaths]=useState();
    async  function getDataApi(){
        const res=await axios.get(`https://api.covid19api.com/summary`)
       // console.log(res.data.Countries[76]);
        setCountry(res.data.Countries[76].Country);
        setConfirmed(res.data.Countries[76].TotalConfirmed);
        setRecovered(res.data.Countries[76].TotalRecovered);
        setDeaths(res.data.Countries[76].TotalDeaths);
       
    }
    getDataApi();

    return(
         <>
         <div className="container  text-center">
         <div className="row">
         <div className="col-4 col-8 bg-primary text-light d-flex mx-auto   mt-5 mb-5 ">
         <div className="row">
         <div className="col-lg-3 col-md-3 col-3">
                 <h6 className="mt-3">Country</h6>
                    <h3 className="p-2 my-5 mx-5 " id="">{Country}</h3>
                 </div>
             <div className="col-lg-3 col-md-3 col-3">
            <h6 className="mt-3">TotalConfirmed</h6>
                <h3 className="p-2 my-5 mx-5 " id="">{TotalConfirmed}</h3>
             </div>
             <div className="col-lg-3 col-md-3 col-3">
             <h6 className="mt-3">TotalRecovered</h6>
            <h3 className="p-2 my-5 mx-5 " id="">{TotalRecovered}</h3>
                 
                 </div>
                 <div className="col-lg-3 col-md-3 col-3">
                 <h6 className="mt-3">TotalDeaths</h6>
                    <h3 className="p-2 my-5 mx-5 gx-2" id="">{TotalDeaths}</h3>
                 </div>

                
         </div>

         </div>    
         </div>
         </div>
        </>
    );
};
export default Corona;
